css:
.print-format {
  font-family: Arial, sans-serif;
  width: 100%;
  margin: 0 auto;
  border: 1px solid #000;
  padding: 15px;
}
.header-section, .footer-section {
  text-align: center;
  margin-bottom: 20px;
}
.transport-details, .billing-shipping-details, .product-details, .footer-section {
  margin-top: 20px;
}
.billing-shipping-details {
  display: flex;
  justify-content: space-between;
}
.billing, .shipping {
  width: 48%;
}
.billing h4, .shipping h4 {
  margin-bottom: 10px;
  font-size: 1em;
}
.product-details {
  width: 100%;
  margin-top: 20px;
  border-collapse: collapse;
  text-align: left;
}
.product-details th, .product-details td {
  border: 1px solid #000;
  padding: 8px;
}
.product-details th {
  background-color: #f2f2f2;
}
.footer-section p {
  margin: 5px 0;
}
.totals {
  display: flex;
  justify-content: space-between;
}
.totals div {
  width: 48%;
}
