script:
frappe.ui.form.on('Sales Issue Voucher', {
	refresh(frm) {
	    frappe.call({
       		method: "get_address_by_account_code",
        		args: {
        		    account_code : frm.doc.account_code
        		},
        		callback: (r)=> {
        		var address = r.message;
        		frm.set_query("sub_account", function() {
                    return {
                        filters: {
            					name: ["in", address],
            			},
                    };
                });
        		}
	        });


	},

	account_code(frm) {
	    frappe.call({
       		method: "get_address_by_account_code",
        		args: {
        		    account_code : frm.doc.account_code
        		},
        		callback: (r)=> {
        		var address = r.message;
        		frm.set_query("sub_account", function() {
                    return {
                        filters: {
            					name: ["in", address],
            			},
                    };
                });
        		}
	        });


	}
})

frappe.ui.form.on('Sales Invoice', {
    refresh: function (frm) {
        // This function will run when the form is refreshed
        // You can initialize or manipulate fields here if needed
    },
    onload: function (frm) {

    },
    custom_sales_type : function (frm) {
        if(frm.doc.custom_sales_type=="Labour Bill") {
            frm.set_value("custom_sales_type_prefix", "LB");
        }
        else if(frm.doc.custom_sales_type=="On Approval") {
            frm.set_value("custom_sales_type_prefix", "OAI");
        }
        else if(frm.doc.custom_sales_type=="Sales Bill") {
            frm.set_value("custom_sales_type_prefix", "SB");
        }
        else if(frm.doc.custom_sales_type=="Rent Bill") {
            frm.set_value("custom_sales_type_prefix", "RB");
        }
        else if(frm.doc.custom_sales_type=="Invoice") {
            frm.set_value("custom_sales_type_prefix", "IB");
        }
        else if(frm.doc.custom_sales_type=="Bill Heading") {
            frm.set_value("custom_sales_type_prefix", "BH");
        }
        else if(frm.doc.custom_sales_type=="Hallmark Issue") {
            frm.set_value("custom_sales_type_prefix", "HI");
        }
        else if(frm.doc.custom_sales_type=="Sales Debit Note") {
            frm.set_value("custom_sales_type_prefix", "SDN");
        }
        else if(frm.doc.custom_sales_type=="Sales Credit Note") {
            frm.set_value("custom_sales_type_prefix", "SCN");
        }
        else if(frm.doc.custom_sales_type=="Issue Voucher") {
            frm.set_value("custom_sales_type_prefix", "IV");
        }
        else if(frm.doc.custom_sales_type=="Delivery Challan") {
            frm.set_value("custom_sales_type_prefix", "DC");
        }
        else if(frm.doc.custom_sales_type=="Bill Heading") {
            frm.set_value("custom_sales_type_prefix", "BH");
        }
    }
});
frappe.ui.form.on('Sales Invoice', {
    before_save: function(frm) {
        if (frm.doc.custom_sales_type=="On Approval" || frm.doc.custom_sales_type=="Issue Voucher" || frm.doc.custom_sales_type=="Hallmark Issue" || frm.doc.custom_sales_type=="Delivery Challan") {
            // Remove TCS related fields if the condition is not met
            frm.doc.tcs_amount = 0;
            frm.doc.tcs_rate = 0;
            frm.refresh_fields(); // Refresh the form to apply changes
        }
        if(frm.doc.custom_sales_type=="Labour Bill") {
            frm.set_value("custom_sales_type_prefix", "LB");
        }
        else if(frm.doc.custom_sales_type=="On Approval") {
            frm.set_value("custom_sales_type_prefix", "OAI");
        }
        else if(frm.doc.custom_sales_type=="Sales Bill") {
            frm.set_value("custom_sales_type_prefix", "SB");
        }
        else if(frm.doc.custom_sales_type=="Rent Bill") {
            frm.set_value("custom_sales_type_prefix", "RB");
        }
        else if(frm.doc.custom_sales_type=="Invoice") {
            frm.set_value("custom_sales_type_prefix", "IB");
        }
        else if(frm.doc.custom_sales_type=="Bill Heading") {
            frm.set_value("custom_sales_type_prefix", "BH");
        }
        else if(frm.doc.custom_sales_type=="Hallmark Issue") {
            frm.set_value("custom_sales_type_prefix", "HI");
        }
        else if(frm.doc.custom_sales_type=="Sales Debit Note") {
            frm.set_value("custom_sales_type_prefix", "SDN");
        }
        else if(frm.doc.custom_sales_type=="Sales Credit Note") {
            frm.set_value("custom_sales_type_prefix", "SCN");
        }
        else if(frm.doc.custom_sales_type=="Issue Voucher") {
            frm.set_value("custom_sales_type_prefix", "IV");
        }
        else if(frm.doc.custom_sales_type=="Delivery Challan") {
            frm.set_value("custom_sales_type_prefix", "DC");
        }
        else if(frm.doc.custom_sales_type=="Bill Heading") {
            frm.set_value("custom_sales_type_prefix", "BH");
        }
    },
});
frappe.ui.form.on('Sales Invoice', {
    refresh(frm) {
        setTimeout(() => {
            frm.remove_custom_button('Quality Inspection(s)', 'Create');
            frm.remove_custom_button('Fetch Timesheet');
            }, 10);
    }
});
frappe.ui.form.on("Sales Invoice", "validate", function(frm) {
    cur_frm.set_value("custom_created_by", frm.doc.owner);
});

frappe.listview_settings['Sales Invoice'] = {
    button: {
        show: function(doc) {
            return true;
        },
        get_label: function() {
            return __('<i class="fa fa-file-pdf-o" style="font-size:18px;color:red"></i>');
        },
        get_description: function(doc) {
            return __('Print {0}', [doc.name])
        },
        action: function(doc) {
            //frappe.set_route("/app/print/Purchase Invoice/" + doc.name);

            var objWindowOpenResult = window.open(frappe.urllib.get_full_url("/api/method/frappe.utils.print_format.download_pdf?"
              + "doctype=" + encodeURIComponent("Sales Invoice")
              + "&name=" + encodeURIComponent(doc.name)
              + "&trigger_print=0"
              + "&format=Test Issue Voucher(Delivery Challan)"
              + "&no_letterhead=0"
              + "&_lang=en"
            ));

            if(!objWindowOpenResult) {
              msgprint(__("Please set permission for pop-up windows in your browser!")); return;
            }
        }
    },

    onload: function (listview) {
        listview.page.add_action_item(__('Open in View-Only'), function () {
            // Get the selected records
            const selected = listview.get_checked_items();

            // Validate that exactly one record is selected
            if (selected.length !== 1) {
                frappe.msgprint(__('Please select exactly one record to open in view-only mode.'));
                return;
            }

            const docname = selected[0].name;

            // Open the selected record in view-only mode
            frappe.set_route('Form', 'Sales Invoice', docname).then(() => {
                frappe.ui.form.on('Sales Invoice', 'refresh', function (frm) {
                    // Set the main form to read-only
                    frm.set_read_only();

                    // Make the 'items' child table read-only
                    if (frm.fields_dict.items && frm.fields_dict.items.grid) {
                        frm.fields_dict.items.grid.make_read_only();
                    }

                    // Make the 'taxes' child table read-only
                    if (frm.fields_dict.taxes && frm.fields_dict.taxes.grid) {
                        frm.fields_dict.taxes.grid.make_read_only();
                    }
                });
            });
        });

        // Add "Print Record" Button
        listview.page.add_action_item(__('Print Record'), function () {
            // Get the selected records
            const selected = listview.get_checked_items();

            // Validate that exactly one record is selected
            if (selected.length !== 1) {
                frappe.msgprint(__('Please select exactly one record to print.'));
                return;
            }

            const docname = selected[0].name;

            // Construct the URL for printing
            const print_url = `/app/print/Sales%20Invoice/${encodeURIComponent(docname)}`;

            // Open the URL in a new tab
            const print_window = window.open(print_url, '_blank');

            // Validate pop-up blockers
            if (!print_window) {
                frappe.msgprint(__('Please enable pop-ups in your browser to open the print view.'));
            }
        });

        // Add "pdf Record" Button
        listview.page.add_action_item(__('Pdf'), function () {
            // Get the selected records
            const selected = listview.get_checked_items();

            // Validate that exactly one record is selected
            if (selected.length !== 1) {
                frappe.msgprint(__('Please select exactly one record to PDF.'));
                return;
            }

            const docname = selected[0].name;

            var objWindowOpenResult = window.open(frappe.urllib.get_full_url("/api/method/frappe.utils.print_format.download_pdf?"
              + "doctype=" + encodeURIComponent("Sales Invoice")
              + "&name=" + encodeURIComponent(docname)
              + "&trigger_print=0"
              + "&format=Test Issue Voucher(Delivery Challan)"
              + "&no_letterhead=0"
              + "&_lang=en"
            ));

            if(!objWindowOpenResult) {
              msgprint(__("Please set permission for pop-up windows in your browser!")); return;
            }
        });

        // Add "Whatsapp Record" Button
        listview.page.add_action_item(__('Whatsapp'), function () {
            // Get the selected records
            const selected = listview.get_checked_items();

            // Validate that exactly one record is selected
            if (selected.length !== 1) {
                frappe.msgprint(__('Please select exactly one record to sen whatsapp messgae.'));
                return;
            }

            const docname = selected[0].name;

            frappe.call({
                        method: 'ascra_billing.ascra_billing.doc_events.sales_invoice.send_whatsapp_msg',
                        args: {
                            phone_number: '7208283676',
                            header_values: ["ASCRA TECHNOLOGIES"],
                            body_values: [docname],
                            button_values: ["256661"]
                        },
                        callback: function(r) {
                            if (r.message && r.message.success) {
                                frappe.msgprint(__('Message sent successfully.'));
                            } else {
                                frappe.msgprint(__('Failed to send message. Details: ') + r.message.error);
                            }
                        }
                    });
        });
    }
};
