doc.net_bill_amt = total_amt + tds_amount

total_net_wt = 0
for item in doc.item_details:
    total_net_wt = total_net_wt + float(item.net_wt)

total_row = doc.item_details[-1]
if total_row.get("category") == "Total":
    doc.item_details.pop()

doc.append("item_details", {
    "category": "Total",
    "net_wt": total_net_wt,
    "peices": '',
    "melting": '',
    "wastage": '',
    "fine": '',
    "other_charges": ''
})

